import React,{useState,useEffect} from 'react';
//import {NavLink} from "react-router-dom";
import "./Navbar.css"
import {Link} from "react-scroll"

const Navbar = () => {

  const [navBackground, setNavBackground] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setNavBackground(window.scrollY > 100);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className='nav'>
        <div className={navBackground ? "navbar active" : "navbar"}>
            <div className="logo">
                <img src="./images/logo.png" alt="logo" className='logo'/>
            </div>
            <div className="menu">
                <ul>
                  <li><Link to='landing' smooth={true} offset={0} duration={500}>HOME</Link></li>
                  <li><Link to='about' smooth={true} offset={0} duration={500}>ABOUT</Link></li>
                  <li><Link to='faq' smooth={true} offset={0} duration={500}>FAQ</Link></li>
               
                <button className="login"><Link to='landing' smooth={true} offset={0} duration={500}>LOGIN</Link></button>
                <button className="signup"><Link to='landing' smooth={true} offset={0} duration={500}>SIGNUP</Link></button>
                </ul>
            </div>
        </div>
      
    </div>
  )
}

export default Navbar
